drop database newspaper;
create database newspaper;
use newspaper;

create table users(
    id int primary key auto_increment,
    name varchar(100),
	username varchar(100),
	password varchar(100),
	email varchar(100),
	tagline text,
	isAuthor boolean
);

create table articles(
    id int primary key auto_increment,
    title varchar(100),
	text text,
	tags text,
	imgURL text,
    date date,
    idAuthor int,
    foreign key (idAuthor) references users(id)
);

create table comments(
    id int primary key auto_increment,
    text text,
	date date,
	idAuthor int,
    idArticle int,
    foreign key (idAuthor) references users(id),
    foreign key (idArticle) references articles(id)
);



